function $designerDeleteSchema(id) { 
  this.require('logger').level('warn');
  var search = $db._Schema.find({ '_id': id }),
    modelName = '',
    modelId = '';

  if (search.length) {
    modelName = search[0]._name;
    $db._Schema.remove({ '_id': id });

    search = $db._Model.find({ '_name': modelName });
    if (search.length) {
      modelId = search[0]._id;
      $db._Model.remove({ '_id': modelId });
      $component.removeFromMemory(modelName);
    }

    search = $db._GeneratedModel.find({ '_name': modelName });
    if (search.length) {
      modelId = search[0]._id;
      $db._GeneratedModel.remove({ '_id': modelId });
      $component.removeFromMemory(modelName);
    }
    this.require('metamodel').create();
  }
  this.require('logger').level('debug');	

  this.require('logger').debug(modelName + ' class has been deleted');
}

