      this.require('logger').debug(behavior.state  + ' behavior of the system has been updated');
      this.require('logger').debug('restarting the system...');
      this.require(behavior.component).start();
    } else {
      this.require('logger').debug(behavior.state  + ' behavior (\'' + behavior.component + '\' component) has been updated');
    }
  }	else {
    this.require('logger').debug('can not update ' + behavior.state  + ' behavior (\'' + behavior.component + '\' component): the component does not exist');
  }
}

