function start() {
  if (typeof document !== 'undefined') {
    runtime.require('logger').level('debug');

    document.addEventListener('dragenter', function dragenter(e) {
      e.stopPropagation();
      e.preventDefault();
    }, false);

    document.addEventListener('dragover', function dragover(e) {
      e.stopPropagation();
      e.preventDefault();
    }, false);

    document.addEventListener('drop', function drop(e) {
      e.stopPropagation();
      e.preventDefault();
      let files = e.dataTransfer.files;
      let reader = new FileReader();
      let json = '';
      reader.onload = function onload(event) {
        json += event.target.result;
      };
      reader.onloadend = function onloadend() {
        try {
          let sys = JSON.parse(json);
          runtime.install(sys);
          runtime.require('logger').info('system ' + sys._id + ' has been installed.');
        } catch (e) {
          runtime.require('logger').warn('try to install an invalid system.');
        }
      };
      reader.readAsText(files[0], 'UTF-8');
    });
  }
}
