function set(key, value) {
  var store = this.store(),
    item = {};

  store[key] = value;
  this.store(store);

  item[key] = JSON.stringify(value);

  try {
    switch (true) {
      case typeof localStorage !== 'undefined':
        localStorage.setItem(key, JSON.stringify(value));
        break;
      default:
        break;
    }
  } catch (e) { }
}

