function init(conf) {
  try {
    switch (true) {
      case typeof localStorage !== 'undefined':
        // init 
        var keys = Object.keys(localStorage),
          store = {},
          i = 0,
          length = 0;

        length = keys.length;
        for (i = 0; i < length; i++) {
          try {
            store[keys[i]] = JSON.parse(localStorage[keys[i]]);
          } catch (e) { }
        }
        this.store(store);

        // event
        window.addEventListener('storage', function (e) {
          var obj = {},
            store = this.store();

          try {
            store[e.key] = JSON.parse(e.newValue);
            this.store(store);

            obj[e.key] = {};
            obj[e.key].oldValue = JSON.parse(e.oldValue);
            obj[e.key].newValue = JSON.parse(e.newValue);

            this.changed(obj);
          } catch (e) { }
        }.bind(this));
        break;
      default:
        break;
    }
  } catch (e) { }
}

