function remove(key) {
  var store = this.store();

  delete store[key];
  this.store(store);

  try {
    switch (true) {
      case typeof localStorage !== 'undefined':
        localStorage.removeItem(key);
        break;
      default:
        break;
    }
  } catch (e) { }
}
